import React from "react";
import "../styles/homepage.css";

const HalfWidthText = ({ article }) => {
  return (
    <div className="half-width-article flex mb-8">
      <img
        src={article.multimedia[0]?.url || ""}
        alt={article.title}
        className="article-image w-1/2 mr-4"
      />
      <div className="text-content w-1/2">
        <h2>{article.title}</h2>
        <p>{article.abstract}</p>
      </div>
    </div>
  );
};

export default HalfWidthText;
