import React, { useState, useEffect } from "react";
import axios from "axios";

const ArticleLayout = () => {
  const [isHovered, setIsHovered] = useState(false);
  const [articles, setArticles] = useState([]);
  const [currentArticle, setCurrentArticle] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get("https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS");
        setArticles(response.data.results);
        setCurrentArticle(response.data.results[0]);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch articles");
        setLoading(false);
      }
    };

    fetchArticles();
  }, []);

  if (loading) return <div className="text-center p-4">Loading...</div>;
  if (error) return <div className="text-center p-4 text-red-500">{error}</div>;
  if (!currentArticle) return <div className="text-center p-4">No article found</div>;

  return (
    <article className="max-w-7xl mx-auto p-4 bg-gradient-to-br from-gray-50 to-gray-100 shadow-lg hover:shadow-xl transition-shadow duration-300 ease-in-out">
      <div className="flex flex-col md:flex-row md:items-start md:space-x-8">
        <div className="flex-1 mb-6 md:mb-0">
          <a 
            href={currentArticle.url}
            target="_blank"
            rel="noopener noreferrer"
            className={`block text-3xl font-bold mb-4 text-gray-800 hover:text-blue-600 transition-colors duration-300 ${isHovered ? "text-blue-600" : ""}`}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
          >
            {currentArticle.title}
          </a>
          
          <p className="text-gray-700 leading-relaxed mb-4 text-lg line-clamp-5">
            {currentArticle.abstract}
          </p>

          <div className="mt-8 pt-4 border-t border-gray-200">
            {articles[1] && (
              <a
                href={articles[1].url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-xl font-semibold text-gray-800 hover:text-blue-600 transition-colors duration-300 cursor-pointer"
              >
                Next Article: {articles[1].title}
              </a>
            )}
          </div>
        </div>

        <div className="md:w-[520px] h-full">
          <div className="relative overflow-hidden shadow-md group">
            <img
              src={currentArticle.multimedia?.[0]?.url || "https://images.unsplash.com/photo-1618005182384-a83a8bd57fbe?ixlib=rb-4.0.3"}
              alt={currentArticle.multimedia?.[0]?.caption || "Article image"}
              className="w-full max-w-[520px] h-[300px] md:h-[400px] object-cover transform group-hover:scale-105 transition-transform duration-300 ease-in-out"
              onError={(e) => {
                e.target.src = "https://images.unsplash.com/photo-1618005182384-a83a8bd57fbe?ixlib=rb-4.0.3";
              }}
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
          </div>
        </div>
      </div>
    </article>
  );
};

export default ArticleLayout;
