import React from "react";
import { useGlobalContext } from "../context";
import { Link } from "react-router-dom";

const ArticleSearch = ({
  byline,
  abstract,
  section_name,
  pub_date,
  headline,
  web_url,
}) => {
  const { formatSection } = useGlobalContext();

  const formatDate = (date) => {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
    };

    return new Date(date).toLocaleDateString("en-En", options);
  };

    return (
      <div className="flex flex-row gap-4 w-[840px] h-[195px] mx-auto mt-4">
        {/* Date section on the left */}
        <div className="flex flex-col items-center justify-start pt-2 w-[80px] text-gray-600">
          <div className="text-sm text-center font-medium">
          </div>
        </div>

        {/* Main article content */}
        <div className="flex flex-col md:flex-row justify-between items-center w-[715px] h-[214px] pb-2 border-b-2 border-gray-300">
          <div className="w-full md:w-[635px] h-auto flex flex-col justify-between">
            <div className="space-y-3">
              <span className="inline-block px-3 py-1 text-sm font-bold text-center text-black">
              {formatSection(section_name)}
              </span>

              <a 
                href="#" 
                target="_blank" 
                rel="noopener noreferrer"
                className="block text-md md:text-lg font-extrabold uppercase text-left hover:text-blue-600 transition-colors duration-300"
              >
                {title}
              </a>

              <p className="text-sm text-justify max-w-[95%]">
              {abstract}
              </p>
            </div>

            <div className="flex justify-start items-center text-xs mt-4">
              <span className="font-bold">{byline.original}</span>
            </div>
          </div>

          <div className="w-full md:w-[205px] h-[195px] mt-4 md:mt-0">
            <img
              src={``}
              alt="Article featured image"
              className="w-full h-full object-cover shadow-lg"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "https://images.unsplash.com/photo-1633613286991-611fe299c4be";
              }}
            />
          </div>
        </div>
      </div>
    );
    };

    export default ArticleSearch;
