import React from "react";
import { useFetchData } from "../Hooks/useFetchData";
import { BounceLoader } from "react-spinners";
import "../pages/SectionPage";
import "../pages/homepage.css";
import Article from "../components/Article";
import SectionOne from "../components/SectionOne";
import GridFourTitle from "../components/GridFourTitle";
import SlideshowNoimage from "../components/SlideshowNoimage";
import ArticleRightSlide from "../components/ArticleRightSlide";
import ArticlesProva from "../components/ArticlesProva";
import InCaseYouMissIt from "../components/InCaseYouMissIt";
import OnlyTitle from "../components/OnlyTitle";
import ArticleText from "../components/ArticleText";
import SectionWell from "../components/SectionWell";


const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;
  const { load, articles } = useFetchData(urlHome);
  const articlesLoad = articles?.results || [];

  if (load) {
    return (

      <div className="homepage-bottomsection">
        <div className="homepage-topsection" >

        <div className="left-column">
          <SectionOne articles={articlesLoad} />
          <div className="divider"></div>
          <GridFourTitle articles={articlesLoad} />
          <div className="divider"></div>
          <ArticlesProva articles={articlesLoad} />
          <div className="divider"></div>
          <ArticleText articles={articlesLoad} />
          <div className="divider"></div>
          <ArticlesProva articles={articlesLoad} />       
        </div>

        {/* Colonna destra */}
        <div className="right-column">
        {articlesLoad.length > 1 && (
            <div className="article half-width">
              <Article
                multimedia={articlesLoad[8].multimedia}
                title={articlesLoad[8].title}
                abstract={articlesLoad[8].abstract}
                url={articlesLoad[8].url}
                byline={articlesLoad[8].byline}
              />
            </div>
          )}
          <ArticleRightSlide articles={articlesLoad} />
          <div className="divider"></div>
          <OnlyTitle articles={articlesLoad} />
          <div className="divider"></div>
          <SlideshowNoimage articles={articlesLoad}/>
          <div className="divider"></div>
          <OnlyTitle articles={articlesLoad} />
          <div className="divider"></div>
          <InCaseYouMissIt articles={articlesLoad} />

        </div>
        </div>
        <div className="divider"></div>
        <SectionWell articles={articlesLoad} />
      </div>
    );
  } else {
    return <div>
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    </div>;
  }
};

export default Homepage;
