const ArticleCard = ({ article, layout = "half-width" }) => {
    const { multimedia, title, abstract, url, section } = article;
  
    if (!article) return null;
  
    return (
      <div className={`article ${layout}`}>
        {layout === "half-width" && (
          <>
            <img src={multimedia?.[0]?.url || ""} alt={title} />
            <div className="text-content">
              <Link to={url} className="article-title">
                <h2>{title}</h2>
              </Link>
              <p>{abstract}</p>
            </div>
          </>
        )}
        {layout === "full-text" && (
          <div className="text-content">
            <h2>{title}</h2>
            <p>{abstract}</p>
            <Link to={url}>Read more</Link>
          </div>
        )}
      </div>
    );
  };
