import React, { useState, useEffect } from "react";
import { FiSearch, FiMenu, FiX, FiChevronRight, FiChevronDown, FiUser } from "react-icons/fi";
import { FaTimes, FaUser } from "react-icons/fa";

const Navbar = () => {
  const [isAccountMenuOpen, setIsAccountMenuOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
const [search, setSearch] = useState("");
  const [error, setError] = useState(null);
  const [isMobile, setIsMobile] = useState(false);
  const [isTablet, setIsTablet] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [isSearchOpen, setIsSearchOpen] = useState(false);


  const topMenuItems = ["U.S.", "INTERNATIONAL", "CANADA", "ESPAñOL", "中文"];

  const menuItems = [
    {
      name: "U.S.",
      submenu: ["Politics", "Economy", "Society", "Education", "Health"]
    },
    {
      name: "World",
      submenu: ["Europe", "Asia", "Middle East", "Africa", "Americas"]
    },
    {
      name: "Business",
      submenu: ["Markets", "Economy", "Companies", "Technology", "Finance"]
    },
    {
      name: "Arts",
      submenu: ["Movies", "Theater", "Music", "Dance", "Visual Arts"]
    },
    {
      name: "Lifestyle",
      submenu: ["Food", "Travel", "Fashion", "Design", "Well-being"]
    },
    {
      name: "Opinion",
      submenu: ["Editorials", "Op-Ed", "Letters", "Sunday Review", "Video"]
    },
    {
      name: "More",
      submenu: ["Audio", "Games", "Cooking", "Wirecutter", "The Athletic"]
    }
  ];

  const horizontalMenuItems = ["U.S.", "World", "Business", "Arts", "Lifestyle", "Opinion", "Audio", "Games", "Cooking", "Wirecutter", "The Athletic"];

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
      setIsTablet(window.innerWidth > 768 && window.innerWidth <= 1024);
    };

    handleResize();
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const formatDate = (date) => {
    return date.toLocaleDateString("it-IT", {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric"
    });
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      if (searchQuery.trim() === "") {
        throw new Error("Please enter a search term");
      }
    } catch (err) {
      setError(err.message);
      setTimeout(() => setError(null), 3000);
    }
  };

  const toggleAccountMenu = () => {
    setIsAccountMenuOpen(!isAccountMenuOpen);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };

  const toggleSearch = () => {
    setIsSearchOpen(!isSearchOpen);
  };

  return (
    <nav className="w-full bg-white shadow-lg" role="navigation" aria-label="Main navigation">
      <div className="max-w-7xl mx-auto">
        {/* Top Section */}
        <div className="px-4 py-3 flex items-center justify-between border-b-0">
          <div className="flex items-center space-x-4 flex-grow md:flex-grow-0">
            {isMobile && (
              <button
                onClick={toggleMobileMenu}
                className="p-2 hover:bg-gray-100 rounded-full transition-colors md:hidden"
                aria-label="Toggle menu"
              >
                <FiMenu className="w-6 h-6" />
              </button>
            )}

            {/* Mobile and Tablet Logo */}
            {isMobile && (
              <div className="flex items-center justify-center space-x-2 flex-grow max-w-2xl">
                <div>
                <img
                  src= "/Title.png" // Logo del sito New York Times
                  alt="Logo"
                  className="w-[300px] h-[110px] object-contain"
                />
                </div>
              </div>
            )}
            {!isMobile && (
              <div className="relative flex items-center space-x-2">
                <button
                  onClick={toggleSearch}
                  className="p-2 hover:bg-gray-100 rounded-full transition-colors"
                  aria-label={isSearchOpen ? "Close search" : "Open search"}
                >
                  <FiSearch className="w-4 h-4" />
                </button>
                {isSearchOpen && (
                  <form onSubmit={handleSearch} className="w-full flex items-center space-x-2">
                    <div className="relative flex-grow">
                      <input
                        type="text"
                        className="w-[182px] h-[31px] pl-2 pr-4 py-2 rounded-[3px] border border-gray-400 focus:outline-none focus:border-blue-500 transition-colors"
                        placeholder="SEARCH"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        aria-label="SEARCH"
                      />

                    </div>
                    <button
                      type="submit"
                      className="px-4 py-2 bg-custom-light-blue text-white text-[11px] rounded-lg hover:bg-opacity-90 transition-colors"
                    >
                      GO
                    </button>
                  </form>
                )}
              </div>
            )}
          </div>

          {/* New Top Menu Items - Desktop Only */}
          {!isMobile && !isTablet && (
            <div className="absolute left-1/2 transform -translate-x-1/2 flex items-center justify-center space-x-6 w-1/2">
              {topMenuItems.map((item, index) => (
                <button
                  key={index}
                  className="text-xs text-gray-700 hover:text-gray-900 transition-colors"
                >
                  {item}
                </button>
              ))}
            </div>
          )}

          <div className="flex items-center space-x-4 ml-4">
            {!isTablet && (
              <button className="text-[10px] bg-[#326891] text-white font-semibold rounded-[3px] hover:bg-opacity-90 transition-colors hidden lg:block px-2 py-2">
                SUBSCRIBE FOR $0.50/WEEK
              </button>
            )}
            <div className="relative">
              <button
                className="flex items-center space-x-2 text-gray-700 hover:text-gray-900 transition-colors"
                onClick={toggleAccountMenu}
                aria-expanded={isAccountMenuOpen}
                aria-haspopup="true"
              >
                {isMobile ? (
                  <FaUser className="w-5 h-5" />
                ) : (
                  <>
                    <span className="text-[13px]">Account</span>
                    <FiChevronDown className="w-5 h-5" />
                  </>
                )}
              </button>

              {isAccountMenuOpen && (
                <div className="fixed top-0 right-0 h-[728px] w-[375px] bg-white shadow-xl transform transition-transform duration-300 z-50 overflow-y-auto">
                  {/* Account Menu Content */}
                  <div className="p-3">
                    <div className="flex justify-between items-center mb-8">
                      <h2 className="text-2xl font-bold"></h2>
                      <button onClick={toggleAccountMenu} className="text-gray-500 hover:text-gray-700 focus:outline-none">
                        <FaTimes className="text-xl" />
                      </button>
                    </div>
                  <div className="px-2 py-2 border-gray-200 border-t">
                    <h4 className="text-lg font-bold text-gray-900">Good evening</h4>
                    <p className="mt-2 text-sm text-gray-600">The email you logged in with isn't associated with a News subscription and has limited access to articles.</p>
                    <button className="mt-3 w-full px-4 py-2 bg-black text-white rounded-[3px] hover:bg-blue-700 transition-colors">Subscribe for more access</button>
                      <p className="text-center mt-2 text-sm text-black font-bold">Already subscribed? Try a different email</p>
                      <div className="flex justify-between items-center cursor-pointer hover:bg-gray-50 py-6">
                      <span className="text-[13px] text-gray-900">Account Settings</span>
                      <FiChevronRight className="w-5 h-5 text-gray-500" />
                      </div>
                      <div className="border-b border-gray-200 mt-2 pt-2">
                        <div className="flex justify-between items-center cursor-pointer hover:bg-gray-50 py-2">
                          <span className="text-[13px] font-bold text-gray-900">YOUR CONTENT</span>
                        </div>
                      </div>
                      <div className="flex justify-between items-center cursor-pointer hover:bg-gray-50 py-2">
                        <span className="text-[13px] text-black">Saved Articles</span>
                        <FiChevronRight className="w-5 h-5 text-gray-500" />
                      </div>
                      <div className="flex justify-between items-center cursor-pointer hover:bg-gray-50 py-2 border-t border-gray-200">
                        <span className="text-[13px] text-black">Newsletters</span>
                        <FiChevronRight className="w-5 h-5 text-gray-500" />
                      </div>
                      <div className="flex justify-between items-center cursor-pointer hover:bg-gray-50 py-2">
                        <span className="text-[13px] font-bold text-black">GET SUPPORT</span>
                      </div>
                      <div className="border-t border-gray-200 pt-2">
                        <div className="flex justify-between items-center cursor-pointer hover:bg-gray-50 py-2">
                        <span className="text-[13px] text-black">Help Center</span>
                          <FiChevronRight className="w-5 h-5 text-gray-500" />
                        </div>
                        <div className="flex justify-between items-center cursor-pointer hover:bg-gray-50 py-4">
                        <span className="text-[13px] text-black font-bold underline">Log Out</span>
                        </div>
                      </div>
                  </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Mobile Menu */}
        {isMobileMenuOpen && isMobile && (
          <div className="fixed inset-0 bg-white z-50 overflow-y-auto">
            <div className="p-4">
              <div className="flex justify-between items-center">
                <button
                  onClick={toggleMobileMenu}
                  className="p-2 hover:bg-gray-100 rounded-full transition-colors"
                  aria-label="Close menu"
                >
                  <FiX className="w-6 h-6" />
                </button>
              </div>
              {/* Search Bar in Mobile Menu */}
              <div className="mt-4">
                <form onSubmit={handleSearch} className="relative flex items-center space-x-2">
                  <div className="relative flex-grow">
                    <input
                      type="text"
                      className="w-full pl-10 pr-4 py-2 rounded-[3px] border border-gray-400 focus:outline-none focus:border-blue-500 transition-colors"
                      placeholder="SEARCH"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      aria-label="Search"
                    />
                    <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                  </div>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-600 text-white rounded-[3px] hover:bg-blue-700 transition-colors"
                  >
                    GO
                  </button>
                </form>
              </div>
              <div className="mt-4">
                {menuItems.map((item, index) => (
                  <div key={index} className="border-b border-gray-200">
                    <button
                      className="w-full py-3 px-4 flex justify-between items-center hover:bg-gray-50"
                      onClick={() => toggleDropdown(index)}
                    >
                      <span className="text-lg font-medium text-xs">{item.name}</span>
                      <FiChevronDown
                        className={`w-5 h-5 transform transition-transform ${
                          activeDropdown === index ? "rotate-180" : ""
                        }`}
                      />
                    </button>
                    {activeDropdown === index && (
                      <div className="bg-gray-50 px-4 py-2">
                        {item.submenu.map((subItem, subIndex) => (
                          <div
                            key={subIndex}
                            className="py-2 px-4 text-gray-600 hover:text-gray-900 cursor-pointer"
                          >
                            {subItem}
                          </div>
                        ))}
                        {index === 5 && (
                          <div className="border-t-4 border-gray-900 mt-2 pt-2 font-bold">
                            {menuItems[6].submenu.map((subItem, subIndex) => (
                              <div
                                key={subIndex}
                                className="py-2 px-4 text-gray-600 hover:text-gray-900 cursor-pointer"
                              >
                                {subItem}
                              </div>
                            ))}
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Middle Section with DateTime and Logo */}
        <div className="px-4">
          <div className="flex items-center justify-between">
            <div className="flex flex-col">
              <div className="text-sm font-semibold text-gray-700">{formatDate(new Date())}</div>
              <div className="text-sm font-bold text-gray-900">Today's Paper</div>
            </div>
            {/* Desktop Logo */}
            {!isMobile && <div className="text-3xl font-serif font-bold">
              <img
                  src= "/Title.png" // Logo del sito New York Times
                  alt="Logo"
                  className="w-[785px] h-[105px] object-contain"
                />
              </div>}
            {isMobile ? (
              <div className="text-xs font-semibold text-[#326891] px-2 py-2">
                SUBSCRIBE FOR $0.50/WEEK
              </div>
            ) : (
              <div className="w-[200px]"></div>
            )}
          </div>
        </div>

        {/* Horizontal Menu - Desktop Only */}
        {!isMobile && (
          <div className="px-4 py-3 hidden md:block">
            <div className="text-[13px] text-black flex justify-center space-x-6">
              {horizontalMenuItems.map((item, index) => (
                <button
                  key={index}
                  className="flex items-center space-x-1 text-gray-700 hover:text-gray-900 font-medium transition-colors"
                >
                  <span>{item}</span>
                  <FiChevronDown className="w-3 h-3" />
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Double Black Line - Only visible on desktop - Modified to be thinner */}
        {!isMobile && !isTablet && (
          <>
            <div className="h-[1px] bg-black"></div>
            <div className="h-[1px] bg-black mt-[1px]"></div>
          </>
        )}

        {/* Error Message */}
        {error && (
          <div className="fixed top-4 right-4 bg-red-500 text-white px-6 py-3 rounded-lg shadow-lg" role="alert">
            {error}
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
