import React from "react";
import { useGlobalContext } from "../context";
import { useParams } from "react-router-dom";
import Article from "../components/Article";
import { BounceLoader } from "react-spinners";
import { useFetchData } from "../Hooks/useFetchData";
import { Link } from "react-router-dom";
import "../components/SectionPageStyle.css";
import "../pages/homepage.css";

const SectionMoreNews = () => {
  const { nameSection } = useParams();

  const urlSection = `https://api.nytimes.com/svc/topstories/v2/news.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { formatSection } = useGlobalContext();
  const { load, articles } = useFetchData(urlSection);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="max-w-screen-xl mx-auto px-4 py-4 myfont">
        <h2 className="font-bold ml-2 mt-2 text-xl md:text-2xl lg:text-3xl capitalize">
          {formatSection(nameSection) + " News"}
        </h2>

        {/* Layout a due colonne */}
        <div className="grid grid-cols-1 md:grid-cols-[2fr_1fr] gap-6 mt-6">
          {/* Colonna sinistra */}
          <div className="left-column space-y-6">
            {/* Immagine e articoli con titolo e testo */}
            <div className="flex flex-col md:flex-row items-center gap-6 bg-white p-6 border-b border-gray-300">
              <div className="flex-shrink-0 md:w-1/2">
                <img
                  src={articlesLoad[0]?.multimedia?.[0]?.url || "/placeholder.jpg"}
                  alt={articlesLoad[0]?.title}
                  className="w-full h-auto rounded-md"
                />
              </div>
              <div className="flex-1">
                <h3 className="text-lg font-bold">
                  <Link to={articlesLoad[0]?.url} target="_blank" rel="noreferrer">
                    {articlesLoad[0]?.title}
                  </Link>
                </h3>
                <p className="text-sm text-gray-600 mt-2">
                  {articlesLoad[0]?.abstract}
                </p>
              </div>
            </div>
            {/* Altri tre articoli solo titolo e testo */}
            {articlesLoad.slice(1, 4).map((article, index) => (
              <div
                key={index}
                className="p-4 border-b border-gray-300 bg-gray-50 rounded-md"
              >
                <h3 className="font-semibold text-base">
                  <Link to={article.url} target="_blank" rel="noreferrer">
                    {article.title}
                  </Link>
                </h3>
                <p className="text-sm text-gray-600 mt-2">{article.abstract}</p>
              </div>
            ))}
          </div>

          {/* Colonna destra */}
          <div className="right-column space-y-4">
            {articlesLoad.slice(4, 10).map((article, index) => (
              <div key={index} className="border-b border-gray-300 pb-2">
                <h4 className="font-semibold text-sm">
                  <Link to={article.url} target="_blank" rel="noreferrer">
                    {article.title}
                  </Link>
                </h4>
                <p className="text-xs text-gray-500 mt-1">
                  {article.byline || "Author Unknown"}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }
};

export default SectionMoreNews;
