import React from "react";
import { BounceLoader } from "react-spinners";
import Article from "../components/Article";
import { useFetchData } from "../Hooks/useFetchData";

const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { load, articles } = useFetchData(urlHome);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="homepage-container max-w-[1000px] mx-auto flex justify-between mt-4">
        {/* Colonna sinistra per gli articoli */}
        <div className="left-column w-[830px]">
          {articlesLoad.map((article, index) => {
            return <Article key={index} {...article} />;
          })}
        </div>

        {/* Colonna destra per contenuti aggiuntivi */}
        <div className="right-column w-[340px] ml-4">
          {/* Puoi aggiungere qui una sidebar o altri contenuti */}
          <div className="sidebar">
          {articlesLoad.map((article, index) => {
            return <Article key={index} {...article} />;
          })}
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }
};

export default Homepage;
