import React from "react";
import { useParams } from "react-router-dom";
import { BounceLoader } from "react-spinners";
import ArticleSearch from "../components/ArticleSearch";
import { useFetchData } from "../Hooks/useFetchData";

const SearchResultsPage = () => {
  const { content } = useParams();  // Ottieni il parametro 'content' dalla URL

  // Modifica l'URL per includere il parametro 'content'
  const searchURL = `https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${content}&api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { load, articles } = useFetchData(searchURL);
  const searchResults = articles?.response;  // Assicurati che `articles` non sia undefined

  // Se la ricerca è in corso e non ci sono risultati
  if (load && (!searchResults || searchResults.docs.length < 1)) {
    return (
      <div className="max-w-[1200px] mx-auto mt-2">
        <h2 className="ml-2">
          Showing results for:{" "}
          <span className="text-xl font-bold capitalize">{content}</span>
        </h2>
        <p className="font-bold text-center mt-3 text-md p-10">
          No search results found
        </p>
      </div>
    );
  }

  // Se i dati sono caricati, ma non ancora visibili
  if (load) {
    return (
      <div className="max-w-[1200px] mx-auto">
        <div className="mt-2">
          <h2 className="ml-2">
            Showing results for:{" "}
            <span className="text-xl font-bold capitalize">{content}</span>
          </h2>
        </div>
        {searchResults?.docs.map((article, index) => {
          return <ArticleSearch key={index} {...article} />;
        })}
      </div>
    );
  } else {
    // Se il caricamento è in corso
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }
};

export default SearchResultsPage;
