import React from "react";
import ImageRight from "../components/articles/imageright";
import ImageLeft from "../components/articles/imageleft";
import FullText from "../components/articles/FullText";
import { useFetchData } from "../Hooks/useFetchData";
import "../components/articles/homepage.css";

const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;
  const { load, articles } = useFetchData(urlHome);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="homepage-container max-w-[1000px] mx-auto flex justify-between mt-4">
        {/* Colonna sinistra */}
        <div className="left-column w-[830px]">
          {/* Primo articolo con immagine a destra */}
          {articlesLoad.length > 0 && <ImageRight article={articlesLoad[0]} />}

          {/* Due articoli affiancati (solo titolo e testo) */}
          <div className="two-columns">
            {articlesLoad.slice(1, 3).map((article, index) => (
              <TwoColumnsText key={index} article={article} />
            ))}
          </div>

          {/* Articolo con immagine a sinistra e metÃ  testo */}
          {articlesLoad.length > 3 && (
            <HalfWidthText article={articlesLoad[3]} />
          )}

          {/* Articolo con solo testo */}
          {articlesLoad.length > 4 && (
            <FullText article={articlesLoad[4]} />
          )}
        </div>
      </div>
    );
  } else {
    return <div>Loading...</div>;
  }
};

export default Homepage;
