import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

const ArticleGrid = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [content] = useState("technology"); // Default search term

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch(
          `https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${content}&api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`
        );
        if (!response.ok) {
          throw new Error("Failed to fetch articles");
        }
        const data = await response.json();
        const formattedArticles = data.response.docs.map((article, index) => ({
          id: index + 1,
          title: article.headline.main,
          image: article.multimedia.length > 0
            ? `images.unsplash.com/photo-168268${index}220${index}66-${index}b8a906ca${index}9`
            : "images.unsplash.com/photo-1682686581660-3693f0c588d2",
          alt: article.headline.main,
          url: article.web_url
        }));
        setArticles(formattedArticles);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchArticles();
  }, [content]);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-50 to-indigo-50">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-indigo-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-50 to-indigo-50">
        <div className="text-red-500 text-xl">{error}</div>
      </div>
    );
  }

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-50 to-indigo-50 p-8">
      <section className="max-w-[840px] mx-auto">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
          {articles.map((article) => (
            <motion.article
              key={article.id}
              className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow duration-300 w-full"
              whileHover={{ scale: 1.03 }}
              transition={{ duration: 0.2 }}
            >
              <a href={article.url} target="_blank" rel="noopener noreferrer">
                <div className="relative overflow-hidden aspect-[4/3]">
                  <motion.img
                    src={`https://${article.image}`}
                    alt={article.alt}
                    className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
                    loading="lazy"
                    whileHover={{ scale: 1.1 }}
                    onError={(e) => {
                      e.target.src = "https://images.unsplash.com/photo-1682686581660-3693f0c588d2";
                      e.target.alt = "Fallback image";
                    }}
                  />
                </div>
                <div className="p-4">
                  <h2 className="text-lg font-semibold text-gray-800 hover:text-indigo-600 transition-colors duration-300">
                    {article.title}
                  </h2>
                </div>
              </a>
            </motion.article>
          ))}
        </div>
      </section>
    </div>
  );
};

export default ArticleGrid;
