const ArticlesProva = ({ articles }) => {
  const articlesLoad = articles?.results || [];

  if (!articlesLoad || articlesLoad.length === 0) {
    return <div>No articles available</div>;
  }

  return (
    <div>
      {articlesLoad.map((article, index) => (
        <div key={index} className="article">
          <h2>{article.title}</h2>
          <p>{article.abstract}</p>
          {article.multimedia && article.multimedia[0] && (
            <img src={article.multimedia[0].url} alt={article.title} />
          )}
        </div>
      ))}
    </div>
  );
};

export default ArticlesProva;
