import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { BounceLoader } from "react-spinners";
import { useFetchData } from "../Hooks/useFetchData";

const SearchResultsPage = () => {
  const { content } = useParams();

  const [searchQuery, setSearchQuery] = useState(content || "");
  const [filteredArticles, setFilteredArticles] = useState([]);

  const searchURL = `https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${searchQuery}&api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { load, articles } = useFetchData(searchURL);
  const searchResults = articles?.response?.docs || [];

  // Filtraggio dinamico degli articoli in base al searchQuery
  useEffect(() => {
    if (!searchQuery) {
      setFilteredArticles([]);
    } else {
      setFilteredArticles(searchResults.filter(article =>
        article.headline.main.toLowerCase().includes(searchQuery.toLowerCase())
      ));
    }
  }, [searchQuery, searchResults]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  if (load) {
    return (
      <div className="max-w-[1000px] mx-auto mt-2">
        <h2 className="ml-2 bg-white border border-gray-300 rounded-lg shadow-sm px-4 py-2 flex items-center space-x-2">
          <svg className="w-5 h-5 text-gray-500" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <circle cx="10" cy="10" r="7" />
            <line x1="16" y1="16" x2="21" y2="21" />
          </svg>
          <input
            type="text"
            value={searchQuery}
            onChange={handleSearchChange}
            className="ml-2 w-full bg-transparent outline-none text-lg font-semibold text-gray-800"
            placeholder="Search for articles..."
          />
        </h2>

        <div className="space-y-6 mt-4">
          {filteredArticles.length < 1 ? (
            <p className="font-bold text-center mt-3 text-xl p-10">No search results found</p>
          ) : (
            filteredArticles.map((article, index) => (
              <article
                key={index}
                className="bg-white rounded-lg shadow-md overflow-hidden flex"
                role="article"
              >
                <img
                  src={article.multimedia?.[0]?.url
                    ? `https://www.nytimes.com/${article.multimedia[0].url}`
                    : "https://images.unsplash.com/photo-1633613286991-611fe299c4be"}
                  alt={article.headline?.main || "Article image"}
                  className="w-[205px] h-[195px] object-cover"
                />
                <div className="flex-1 p-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-2">
                    {article.headline?.main}
                  </h2>
                  <div className="flex items-center text-sm text-gray-500 mb-3">
                    <div className="mr-4">
                      <span>{article.byline?.original}</span>
                    </div>
                    <div>
                      <span>{new Date(article.pub_date).toLocaleDateString()}</span>
                    </div>
                  </div>
                  <p className="text-gray-600 mb-4">
                    {article.abstract || article.lead_paragraph}
                  </p>
                  <div className="flex justify-between items-center">
                    <a
                      href={article.web_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-indigo-600 hover:text-indigo-500"
                    >
                      View on NY Times
                    </a>
                  </div>
                </div>
              </article>
            ))
          )}
        </div>
      </div>
    );
  }

  return (
    <BounceLoader
      className="mx-auto my-36"
      color="gray"
      size={180}
      aria-label="Loading Spinner"
    />
  );
};

export default SearchResultsPage;
