import React from "react";
import { Link } from "react-router-dom";
import { useGlobalContext } from "../context";

const RightSideArticle = ({
  section,
  published_date,
  title,
  byline,
  multimedia,
  abstract,
  url,
}) => {
  const { formatSection } = useGlobalContext();

  const article = articlesLoad && articlesLoad.length > 8 ? articlesLoad[8] : null;

  console.log(articlesLoad[8]);

  // Funzione per formattare la data
  const formatDate = (date) => {
    if (!date) return "Unknown Date";
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(date).toLocaleDateString("en-US", options);
  };

  return (
    <div style={{ width: "320px", padding: "20px", border: "1px solid #e0e0e0", borderRadius: "8px", backgroundColor: "#f9f9f9" }}>
      {/* Sezione */}
      {section && (
        <p style={{ fontSize: "0.75rem", fontWeight: "600", textTransform: "uppercase", backgroundColor: "#fff", padding: "4px 8px", borderRadius: "4px", color: "#333", marginBottom: "8px" }}>
          {formatSection(section)}
        </p>
      )}

      {/* Header: Titolo e Immagine */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "16px" }}>
        <h2 style={{ fontSize: "1.25rem", fontWeight: "700", color: "#333", margin: "0", textAlign: "left", flex: 1 }}>
          <Link to={url} target="_blank" rel="noreferrer">
            {title}
          </Link>
        </h2>
        {multimedia && multimedia[0] && (
          <img
            src={multimedia[0].url}
            alt={title}
            style={{ width: "80px", height: "80px", objectFit: "cover", borderRadius: "8px" }}
          />
        )}
      </div>

      {/* Abstract */}
      {abstract && (
        <div style={{ fontSize: "0.875rem", color: "#555", textAlign: "justify", marginBottom: "16px" }}>
          <p>{abstract}</p>
        </div>
      )}

      {/* Data */}
      {published_date && (
        <p style={{ fontSize: "0.75rem", color: "#777", textAlign: "right" }}>{formatDate(published_date)}</p>
      )}
    </div>
  );
};

export default RightSideArticle;
