import React from 'react';
import "./components/style/article.css";

const OneArticleBlock = () => {
  // Dati statici dell'articolo
  const article = {
    title: 'Example Article Title',
    abstract: 'This is a brief summary of the article.',
    url: 'https://example.com',
    byline: 'By John Doe',
    published_date: '2024-11-15T12:00:00Z',
    multimedia: [
      { url: 'https://via.placeholder.com/400x200' } // Placeholder image
    ]
  };

  return (
    <div className="one-article-block">
      <div className="article-content">
        <a href={article.url} target="_blank" rel="noopener noreferrer">
          <h2>{article.title}</h2>
        </a>
        <a href={article.url} target="_blank" rel="noopener noreferrer">
          <p>{article.abstract}</p>
        </a>
        <span className="article-meta">
          {article.byline} &bull; {new Date(article.published_date).toLocaleDateString()}
        </span>
        <span className="read-more">
          <div>
            <a href={article.url} target="_blank" rel="noopener noreferrer">Read More</a>
          </div>
        </span>
      </div>
      <a href={article.url} target="_blank" rel="noopener noreferrer" className="image-link">
        <img src={article.multimedia?.[0]?.url} alt={article.title} className="article-image" />
      </a>
    </div>
  );
};

export default OneArticleBlock;
