import React from "react";
import { Link } from "react-router-dom";
import "../article.css";

const Article = ({ section, published_date, title, byline, multimedia, abstract, url }) => {

  // Funzione per formattare la sezione se non esiste già nel contesto
  const formatSection = (section) => {
    // Se la section è null, undefined o una stringa vuota/bianca, restituisce "No Section"
    if (!section || section.trim() === "") return "No Section";

    // Restituisce la section con la prima lettera maiuscola
    return section.charAt(0).toUpperCase() + section.slice(1);
  };

  // Funzione per formattare la data (se non hai una funzione globale)
  const formatDate = (date) => {
    if (!date) return "Unknown Date";
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(date).toLocaleDateString("en-US", options);
  };

  return (
    <div className="articles-container"> {/* Questo div contiene tutti gli articoli */}
      <div className="container flex flex-col justify-center items-start w-full p-0 m-0 border-b-2 border-gray-300">
        {/* Sezione e Titolo */}
        {section && (
          <p className="text-xs font-bold mt-2 bg-white rounded text-black p-1 uppercase">
            {formatSection(section)}
          </p>
        )}

        {multimedia && multimedia[0] && (
          <div className="mt-2">
            <div className="flex flex-col items-start">
              <img
                className="w-full h-auto shadow-md"
                src={multimedia[0].url}
                alt={title}
              />
              {byline && (
                <p className="mt-2 self-end text-xs font-bold byline">
                  {byline}
                </p>
              )}
            </div>
          </div>
        )}

        {/* Titolo */}
        <h2 className="font-extrabold text-lg w-full mx-auto mt-4 text-left">
          <Link to={url} target="_blank" rel="noreferrer">
            {title}
          </Link>
        </h2>

        {/* Abstract */}
        {abstract && (
          <div className="mt-4 text-justify w-full mx-auto pb-[10px]">
            <p className="text-sm">{abstract}</p>
          </div>
        )}

        <div className="flex flex-row justify-between items-center mt-2">
          {published_date && (
            <p className="font-bold text-xs date">
              {formatDate(published_date)}
            </p>
          )}
        </div>
      </div>
      {/* Aggiungi altri articoli qui */}
    </div>
  );
};

export default Article;
