import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaSearch } from "react-icons/fa";
import { BiErrorCircle } from "react-icons/bi";
import { BiRefresh } from "react-icons/bi";

const ArticleComponent = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [retryCount, setRetryCount] = useState(0);
  const [retryTimer, setRetryTimer] = useState(30);
  const [isRetrying, setIsRetrying] = useState(false);

  const fetchArticles = async (query = "opinion") => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        `https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${query}&api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`,
        { timeout: 5000 }
      );
      setArticles(response.data.response.docs);
      setLoading(false);
      setIsRetrying(false);
    } catch (err) {
      let errorMessage;
      if (err.response && err.response.status === 429) {
        errorMessage = "Too many requests. Please wait before trying again.";
        handleTooManyRequests();
      } else {
        errorMessage = err.response
          ? `Error: ${err.response.status} - ${err.response.statusText}`
          : err.message === "Network Error"
          ? "Network error: Please check your internet connection"
          : "Failed to fetch articles. Please try again later";
      }
      setError(errorMessage);
      setLoading(false);
    }
  };

  const handleTooManyRequests = () => {
    setIsRetrying(true);
    setRetryTimer(30);

    const timer = setInterval(() => {
      setRetryTimer((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          setIsRetrying(false);
          fetchArticles(searchQuery || "opinion");
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const handleRetry = () => {
    if (!isRetrying) {
      setRetryCount((prev) => prev + 1);
      fetchArticles(searchQuery || "opinion");
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      fetchArticles(searchQuery);
    }
  };

  useEffect(() => {
    fetchArticles();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col justify-center items-center h-screen space-y-4 p-4">
        <BiErrorCircle className="text-red-500 text-6xl" />
        <div className="text-red-500 text-xl font-semibold text-center">{error}</div>
        {isRetrying ? (
          <div className="flex flex-col items-center space-y-2">
            <div className="flex items-center space-x-2">
              <BiRefresh className="animate-spin text-blue-500 text-2xl" />
              <span className="text-blue-500">Retrying in {retryTimer} seconds...</span>
            </div>
            <div className="w-64 h-2 bg-gray-200 rounded-full">
              <div
                className="h-full bg-blue-500 rounded-full transition-all duration-1000 ease-linear"
                style={{ width: `${(retryTimer / 30) * 100}%` }}
              ></div>
            </div>
          </div>
        ) : (
          <button
            onClick={handleRetry}
            className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={isRetrying}
          >
            Retry {retryCount > 0 && `(${retryCount})`}
          </button>
        )}
      </div>
    );
  }

  return (
    <div className="flex flex-col w-full max-w-[840px] mx-auto mt-8 px-4">
      <form onSubmit={handleSearch} className="mb-8">
        <div className="flex items-center gap-2 w-full max-w-md mx-auto">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search articles..."
            className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center gap-2"
          >
            <FaSearch />
            Search
          </button>
        </div>
      </form>

      <div className="flex flex-col gap-8">
        {articles.map((article, index) => (
          <div key={index} className="flex flex-row gap-4 w-full h-auto">
            <div className="flex flex-col items-center justify-start pt-2 w-[80px] text-gray-600">
              <div className="text-sm text-center font-medium">
                {new Date(article.pub_date).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric"
                })}
              </div>
            </div>

            <div className="flex flex-col md:flex-row justify-between items-center w-full pb-2 border-b-2 border-gray-300">
              <div className="w-full md:w-[635px] h-auto flex flex-col justify-between">
                <div className="space-y-3">
                  <span className="inline-block px-3 py-1 text-sm font-bold text-center text-black bg-gray-200">
                    {article.section_name}
                  </span>

                  <a
                    href={article.web_url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="block text-md md:text-lg font-extrabold uppercase text-left hover:text-blue-600 transition-colors duration-300"
                  >
                    {article.headline.main}
                  </a>

                  <p className="text-sm text-justify max-w-[95%]">
                    {article.abstract || article.lead_paragraph}
                  </p>
                </div>

                <div className="flex justify-start items-center text-xs mt-4">
                  <span className="font-bold">{article.byline?.original}</span>
                </div>
              </div>

              <div className="w-full md:w-[205px] h-[195px] mt-4 md:mt-0">
                <img
                  src={
                    article.multimedia?.[0]?.url
                      ? `https://www.nytimes.com/${article.multimedia[0].url}`
                      : "https://images.unsplash.com/photo-1633613286991-611fe299c4be"
                  }
                  alt="Article featured image"
                  className="w-full h-full object-cover shadow-lg rounded-lg transition-opacity duration-300 hover:opacity-90"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "https://images.unsplash.com/photo-1633613286991-611fe299c4be";
                  }}
                />
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ArticleComponent;
