import React from "react";
import { motion } from "framer-motion";

const InCaseYouMissIt = () => {
  const articles = [
    {
      id: 1,
      title: "The Future of Artificial Intelligence in Modern Healthcare Systems",
      image: "images.unsplash.com/photo-1576091160399-112ba8d25d1d?ixlib=rb-4.0.3",
      category: "Technology"
    },
    {
      id: 2,
      title: "Sustainable Architecture: Building for a Better Tomorrow",
      image: "images.unsplash.com/photo-1503387762-592deb58ef4e?ixlib=rb-4.0.3",
      category: "Architecture"
    },
    {
      id: 3,
      title: "Global Economic Trends: Analysis and Future Predictions",
      image: "images.unsplash.com/photo-1611974789855-9c2a0a7236a3?ixlib=rb-4.0.3",
      category: "Economy"
    },
    {
      id: 4,
      title: "The Rise of Remote Work: Reshaping Corporate Culture",
      image: "images.unsplash.com/photo-1524749292158-7540c2494485?ixlib=rb-4.0.3",
      category: "Business"
    }
  ];

  return (
    <div className="max-w-3xl mx-auto p-6 space-y-6">
      {articles.map((article) => (
        <motion.div
          key={article.id}
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="flex items-center justify-between p-4 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300 cursor-pointer"
        >
          <div className="flex-1 pr-4">
            <span className="text-sm font-medium text-blue-600 mb-1 block">
              {article.category}
            </span>
            <h3 className="text-lg font-semibold text-gray-800 line-clamp-2 hover:text-blue-600 transition-colors duration-200">
              {article.title}
            </h3>
          </div>
          <div className="flex-shrink-0">
            <img
              src={`https://${article.image}`}
              alt={article.title}
              className="w-[90px] h-[90px] object-cover rounded-lg"
              onError={(e) => {
                e.target.src = "https://images.unsplash.com/photo-1453928582365-b6ad33cbcf64?ixlib=rb-4.0.3";
                e.target.alt = "Fallback image";
              }}
            />
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default InCaseYouMissIt;
