import React from "react";
import "../pages/homepage.css";

const ImageRight = ({ 
  title,
  multimedia,
  abstract
}) => {
  return (
    <div className="image-right-container flex mb-8">
      <div className="text-content w-1/2 pr-4">
        <h2>{title}</h2> {/* Usa il prop title */}
        <p>{abstract}</p> {/* Usa il prop abstract */}
      </div>
      <img
        src={multimedia[0]?.url || ""}
        alt={title}  
        className="article-image w-1/2"
      />
    </div>
  );
};

export default ImageRight;
