import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaSearch } from "react-icons/fa";

const API_KEY = "30mS33uUBWOVAYffuOfb9rpPZhDzpDjS";

const ArticleComponent = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [retryCount, setRetryCount] = useState(0);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);

  // Funzione per chiamare l'API
  const fetchArticles = async (query, page) => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(
        `https://api.nytimes.com/svc/search/v2/articlesearch.json`,
        {
          params: {
            q: query || "news",
            page,
            "api-key": API_KEY,
          },
        }
      );

      const newArticles = response.data.response.docs;
      setArticles((prev) => (page === 0 ? newArticles : [...prev, ...newArticles]));
      setHasMore(newArticles.length > 0);
    } catch (err) {
      setError("An error occurred while fetching articles. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // Gestione della ricerca
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setPage(0);
      setHasMore(true);
      fetchArticles(searchQuery, 0);
    }
  };

  // Caricamento di altri articoli
  const loadMore = () => {
    if (!loading && hasMore) {
      const nextPage = page + 1;
      setPage(nextPage);
      fetchArticles(searchQuery, nextPage);
    }
  };

  // Caricamento iniziale
  useEffect(() => {
    fetchArticles(searchQuery, page);
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <div className="flex flex-col w-full max-w-[840px] mx-auto mt-8 px-4">
      <form onSubmit={handleSearch} className="mb-8">
        <div className="flex items-center gap-2 w-full max-w-md mx-auto">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search articles..."
            className="w-full px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center gap-2"
          >
            <FaSearch />
            Search
          </button>
        </div>
      </form>

      {error && (
        <div className="text-red-500 text-center mb-4">
          {error}
        </div>
      )}

      <div className="flex flex-col gap-8">
        {articles.map((article, index) => (
          <div key={`${article.web_url}-${index}`} className="flex flex-row gap-4 w-full h-auto">
            <div className="flex flex-col items-center justify-start pt-2 w-[80px] text-gray-600">
              <div className="text-sm text-center font-medium">
                {new Date(article.pub_date).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })}
              </div>
            </div>

            <div className="flex flex-col md:flex-row justify-between items-center w-full pb-2 border-b-2 border-gray-300">
              <div className="w-full md:w-[635px] h-auto flex flex-col justify-between">
                <div className="space-y-3">
                  <span className="inline-block px-3 py-1 text-sm font-bold text-center text-black bg-gray-200">
                    {article.section_name}
                  </span>

                  <a
                    href={article.web_url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="block text-md md:text-lg font-extrabold uppercase text-left hover:text-blue-600 transition-colors duration-300"
                  >
                    {article.headline.main}
                  </a>

                  <p className="text-sm text-justify max-w-[95%]">
                    {article.abstract || article.lead_paragraph}
                  </p>
                </div>

                <div className="flex justify-start items-center text-xs mt-4">
                  <span className="font-bold">{article.byline?.original}</span>
                </div>
              </div>

              <div className="w-full md:w-[205px] h-[195px] mt-4 md:mt-0">
                <img
                  src={
                    article.multimedia?.[0]?.url
                      ? `https://www.nytimes.com/${article.multimedia[0].url}`
                      : "https://images.unsplash.com/photo-1633613286991-611fe299c4be"
                  }
                  alt="Article featured image"
                  className="w-full h-full object-cover shadow-lg rounded-lg transition-opacity duration-300 hover:opacity-90"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "https://images.unsplash.com/photo-1633613286991-611fe299c4be";
                  }}
                />
              </div>
            </div>
          </div>
        ))}
      </div>

      {loading && <div className="text-center">Loading...</div>}

      {hasMore && (
        <div className="flex justify-center mt-8 mb-8">
          <button
            onClick={loadMore}
            disabled={loading}
            className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-colors duration-300 disabled:opacity-50"
          >
            {loading ? "Loading..." : "Load More"}
          </button>
        </div>
      )}

      {!hasMore && articles.length > 0 && (
        <div className="text-center text-gray-600 mt-8 mb-8">
          No more articles to load
        </div>
      )}
    </div>
  );
};

export default ArticleComponent;
