import React from "react";
import { useGlobalContext } from "../context";
import { Link } from "react-router-dom";

const ArticleImageRight = ({
  section,
  published_date,
  title,
  byline,
  multimedia,
  abstract,
  url,
  author,
}) => {
  const { formatSection } = useGlobalContext();


  // Funzione per formattare la data
  const formatDate = (date) => {
    if (!date) return "Unknown Date";
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(date).toLocaleDateString("en-US", options);
  };

  return (
    <div className="">
      {/* Sezione e Titolo */}
      {section && (
        <p className="text-xs font-bold mt-2 bg-white text-black p-1 uppercase">
          {formatSection(section)}
        </p>
      )}

      {/* Immagine */}
      {multimedia && multimedia[0] && (
        <div className="relative group overflow-hidden rounded-lg">
          <img
            className="w-full h-[250px] object-cover transition-transform duration-300 group-hover:scale-105"
            src={multimedia[0].url}
            alt={title}
          />
          {byline && (
            <p className="flex items-center space-x-3 text-gray-600">{byline}</p>
          )}
        </div>
      )}

      {/* Titolo */}
      <h2 className="`text-2xl font-bold text-gray-800 transition-colors duration-300">
        <Link to={url} target="_blank" rel="noreferrer">
          {title}
        </Link>
      </h2>

      {/* Abstract */}
      {abstract && (
        <div className="text-gray-600 leading-relaxed">
          <p className="text-sm">{abstract}</p>
        </div>
      )}

      {/* Data */}
      <div className="flex flex-row justify-between items-center mt-2">
        {published_date && (
          <p className="font-bold text-xs">
            {formatDate(published_date)}
          </p>
        )}
      </div>
    </div>
  );
};

export default ArticleImageRight;
