import React from "react";
import ImageRight from "../components/articles/imageright";
import ImageLeft from "../components/articles/imageleft";
import FullWidth from "../components/articles/fullwidth";
import { useFetchData } from "../Hooks/useFetchData";
import "../components/articles/homepage.css";

const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;
  const { load, articles } = useFetchData(urlHome);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="homepage-container max-w-[1200px] mx-auto flex justify-between mt-4">
        {/* Colonna sinistra */}
        <div className="left-column w-[830px]">
          {/* Primo articolo con immagine a destra */}
          {articlesLoad.length > 0 && (
            <>
              <ImageRight article={articlesLoad[0]} />
              <div className="article-divider"></div>
            </>
          )}

          {/* Due articoli successivi con titolo e testo */}
          {articlesLoad.slice(1, 3).map((article, index) => (
            <React.Fragment key={index}>
              <FullWidth article={article} />
              <div className="article-divider"></div>
            </React.Fragment>
          ))}

          {/* Altri articoli alternati */}
          {articlesLoad.slice(3).map((article, index) => (
            <React.Fragment key={index}>
              {index % 2 === 0 ? (
                <ImageLeft article={article} />
              ) : (
                <ImageRight article={article} />
              )}
              <div className="article-divider"></div>
            </React.Fragment>
          ))}
        </div>

        {/* Colonna destra */}
        <div className="right-column w-[340px] ml-4">
          {/* Puoi aggiungere una sidebar o contenuti aggiuntivi */}
          <h3 className="sidebar-title">Contenuti Aggiuntivi</h3>
          {articlesLoad.slice(0, 5).map((article, index) => (
            <div key={index} className="sidebar-article">
              <h4>{article.title}</h4>
              <p>{article.abstract}</p>
            </div>
          ))}
        </div>
      </div>
    );
  } else {
    return <div>Loading...</div>;
  }
};

export default Homepage;
