import React from "react";
import { useParams } from "react-router-dom";
import ArticleFeed from "../components/ArticleFeed";
import { BounceLoader } from "react-spinners";
import { useFetchData } from "../Hooks/useFetchData";

const FeedPage = () => {
  const { topic } = useParams(); // Estrai il parametro 'topic' dalla URL

  // Usa il parametro 'topic' per costruire l'URL dell'API
  const url = `https://api.nytimes.com/svc/news/v3/content/all/${topic}.json?api-key=${process.env.REACT_APP_API_KEY}`;

  const { load, articles } = useFetchData(url);
  const articlesLoad = articles?.results;

  if (load) {
    if (articlesLoad) {
      return (
        <div className="max-w-[1000px] mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-y-4">
          {articlesLoad.map((article, index) => (
            <ArticleFeed key={index} {...article} />
          ))}
        </div>
      );
    } else {
      return <p>No articles found for {topic}</p>;
    }
  } else {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }
};

export default FeedPage;
