import React from "react";
import "../components/ArticleLeftSlide.css";

const ArticleLeftSlide = ({ articles }) => {
  const maxArticles = 4; // Numero massimo di articoli da mostrare

  // Verifica che gli articoli esistano e non siano vuoti
  if (!articles || articles.length === 0) {
    return <div>No articles available</div>;
  }

  // Filtra solo i primi `maxArticles` articoli
  const filteredArticles = articles.slice(0, maxArticles);

  return (
    <div className="grid-container">
      {filteredArticles.map((article, index) => (
        <div key={index} className="grid-item">
          <img
            src={article.multimedia && article.multimedia[0] ? article.multimedia[0].url : ""}
            alt={article.title}
            className="article-image"
          />
          <h2 className="article-title">{article.title}</h2>
          <h3 className="article-byline">{article.byline}</h3>
        </div>
      ))}
    </div>
  );
};

export default ArticleLeftSlide;
