import React from "react";
import { useGlobalContext } from "../context";
import { useParams } from "react-router-dom";
import Article from "../components/Article";
import { BounceLoader } from "react-spinners";
import { useFetchData } from "../Hooks/useFetchData";
import "../components/SectionPageStyle.css"

const SectionPage = () => {
  const { business } = useParams();

  const urlSection = `https://api.nytimes.com/svc/topstories/v2/${business}.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { formatSection } = useGlobalContext();
  const { load, articles } = useFetchData(urlSection);


  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="max-w-screen-xl mx-auto px-4 py-4">
        <h2 className="font-bold ml-2 mt-2 text-xl md:text-2xl lg:text-3xl capitalize">
          {formatSection(nameSection) + " News"}
        </h2>

        {/* Contenitore Grid con 3 colonne ...article*/}
        <div className="grid grid-cols-[610px_320px_240px] gap-4 mt-4">
          {articlesLoad.map((article, index) => (
            <Article key={index} {...article} />
          ))}
        </div>
      </div>
    );
  } else {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }
};

export default SectionPage;
