import React from "react";
import { useGlobalContext } from "../context";
import { Link } from "react-router-dom";
import "./article.css";

const Article = ({
  section,
  title,
  byline,
  multimedia,
  abstract,
  url,
}) => {
  const { formatSection } = useGlobalContext();

  // show news
  if (section !== "admin" && section && title && abstract) {
    return (
      <div className="container flex flex-col justify-center items-center w-[90%] mx-auto pb-2 border-b-2 border-gray-300">
        {/* Section Name */}
        <p className="text-sm font-bold h-fit w-fit mt-2 bg-white rounded text-black p-1 capitalize">
          {formatSection(section)}
        </p>

        {/* Image */}
        {multimedia && multimedia[1] && (
          <div className="mt-4">
            <img
              className="w-full h-auto shadow-md"
              src={multimedia[1].url}
              alt={title}
            />
          </div>
        )}

        {/* Title */}
        <h2 className="font-extrabold uppercase text-lg w-full mx-auto mt-4 text-center">
          <Link to={url} target="_blank" rel="noreferrer">
            {title}
          </Link>
        </h2>

        {/* Abstract/Text */}
        <div className="mt-4 text-justify w-full mx-auto px-4">
          <p className="text-sm">{abstract}</p>
        </div>

        {/* Byline */}
        {byline && (
          <div className="mt-2 flex justify-center">
            <p className="font-bold text-xs">{byline}</p>
          </div>
        )}
      </div>
    );
  }

  return null;
};

export default Article;
