// src/components/ArticleGrid.js
import React from "react";
import { Link } from "react-router-dom";

const GridFourTitle = ({ articles }) => {
  if (!articles || articles.length < 4) {
    return null;
  }

  return (
    <div className="left-column-container grid grid-cols-2 gap-4">
      {/* Articolo 1 */}
      <div className="article">
        <Link to={articles[0].url} className="article-title">
          <h2 className="text-lg font-bold">{articles[9].title}</h2>
        </Link>
      </div>

      {/* Articolo 2 */}
      <div className="article">
        <Link to={articles[1].url} className="article-title">
          <h2 className="text-lg font-bold">{articles[1].title}</h2>
        </Link>
      </div>

      {/* Articolo 3 */}
      <div className="article">
        <Link to={articles[2].url} className="article-title">
          <h2 className="text-lg font-bold">{articles[2].title}</h2>
        </Link>
      </div>

      {/* Articolo 4 */}
      <div className="article">
        <Link to={articles[3].url} className="article-title">
          <h2 className="text-lg font-bold">{articles[3].title}</h2>
        </Link>
      </div>
    </div>
  );
};

export default GridFourTitle;
