import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { BounceLoader } from "react-spinners";
import axios from "axios";

const SearchResultsPage = () => {
  const { content } = useParams();
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  const searchURL = `https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${content}&api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  useEffect(() => {
    const fetchArticles = async () => {
      setLoading(true);
      setError(false);
      try {
        const response = await axios.get(searchURL);
        setArticles(response.data.response.docs || []);
      } catch (error) {
        console.error("Error fetching articles:", error);
        setError(true);
      } finally {
        setLoading(false);
      }
    };

    fetchArticles();
  }, [searchURL]);

  const formatDate = (date) => {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
    };

    return new Date(date).toLocaleDateString("en-EN", options);
  };

  if (loading) {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }

  if (error || articles.length === 0) {
    return (
      <div className="max-w-[1000px] mx-auto mt-2">
        <h2 className="ml-2">
          Showing results for:{" "}
          <span className="text-3xl font-bold capitalize">{content}</span>
        </h2>
        <p className="font-bold text-center mt-3 text-xl p-10">
          No search results found
        </p>
      </div>
    );
  }

  return (
    <div className="max-w-[840px] mx-auto mt-2"> {/* Set the max width to 840px */}
  <h2 className="ml-2 bg-white border border-gray-300 rounded-lg shadow-sm px-4 py-2 flex items-center space-x-2">
  <svg className="w-5 h-5 text-gray-500" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <circle cx="10" cy="10" r="7" />
    <line x1="16" y1="16" x2="21" y2="21" />
  </svg>
  <span className="text-lg font-semibold text-gray-800">
    Showing results for:{" "}
    <span className="text-2xl font-bold capitalize text-indigo-600">{content}</span>
  </span>
</h2>
  <div className="space-y-6 mt-4">
    {articles.map((article, index) => (
      <article
        key={index}
        className="bg-white rounded-lg shadow-md overflow-hidden flex flex-col md:flex-row" // Use flex-row for larger screens
        role="article"
      >
        <div className="flex-1 p-6"> {/* Content container */}
          <h2 className="text-xl font-semibold text-gray-900 mb-2">
            {article.headline?.main}
          </h2>
          <div className="flex items-center text-sm text-gray-500 mb-3">
            <div className="mr-4">
              <span>{article.byline?.original}</span>
            </div>
            <div>
              <span>{formatDate(article.pub_date)}</span>
            </div>
          </div>
          <p className="text-gray-600 mb-4">
            {article.abstract || article.lead_paragraph}
          </p>
          <div className="flex justify-between items-center">
            <a
              href={article.web_url}
              target="_blank"
              rel="noopener noreferrer"
              className="text-indigo-600 hover:text-indigo-500"
            >
              View on NY Times
            </a>
          </div>
        </div>

        <img
          src={
            article.multimedia?.[0]?.url
              ? `https://www.nytimes.com/${article.multimedia[0].url}`
              : "https://images.unsplash.com/photo-1633613286991-611fe299c4be"
          }
          alt={article.headline?.main || "Article image"}
          className="w-[205px] h-[195px] object-cover" // Set width and height of image
          onError={(e) => {
            e.target.onerror = null;
          }}
        />
      </article>
    ))}
  </div>
</div>

  );
};

export default SearchResultsPage;
