import React, { useState, useEffect } from "react";
import Highlights from "./Highlights";  // Importa il componente Highlights
import "../ArticleSlideshow.css";

const Homepage = () => {
  // Stato per articoli completi e articoli filtrati per sezione
  const [articles, setArticles] = useState([]);
  const [filteredArticles, setFilteredArticles] = useState([]);
  const [selectedSection, setSelectedSection] = useState("Tecnologia");  // Sezione predefinita

  // Effettua il fetch degli articoli
  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch("https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS");  // Modifica con il tuo endpoint API
        const data = await response.json();

        // Salva tutti gli articoli nel stato
        setArticles(data.articles);  

        // Filtra gli articoli per la sezione selezionata
        const filtered = data.articles.filter((article) => article.section === selectedSection);
        setFilteredArticles(filtered);  // Salva solo gli articoli della sezione selezionata
      } catch (error) {
        console.error("Errore durante il recupero degli articoli:", error);
      }
    };

    fetchArticles();
  }, [selectedSection]);  // Ricarica gli articoli ogni volta che la sezione cambia

  return (
    <div className="homepage">
      {/* Selettore della sezione */}
      <select
        value={selectedSection}
        onChange={(e) => setSelectedSection(e.target.value)}
        className="section-selector"
      >
        <option value="Tecnologia">Tecnologia</option>
        <option value="Sport">Sport</option>
        <option value="Cultura">Cultura</option>
        {/* Aggiungi altre opzioni di sezione come necessario */}
      </select>

      {/* Colonna sinistra (con articoli filtrati per la sezione) */}
      <div className="left-column">
        {filteredArticles.length > 0 ? (
          filteredArticles.map((article, index) => (
            <div key={index} className="article">
              <img src={article.multimedia[0]?.url || ""} alt={article.title} />
              <h2>{article.title}</h2>
              <p>{article.abstract}</p>
            </div>
          ))
        ) : (
          <p>Nessun articolo trovato per la sezione selezionata.</p>
        )}
      </div>

      {/* Colonna destra con Highlights (sezione filtrata) */}
      <div className="right-column">
        {/* Passiamo gli articoli filtrati come props al componente Highlights */}
        <Highlights articles={filteredArticles} />
      </div>
    </div>
  );
};

export default Homepage;
