import React from "react";
import { useParams } from "react-router-dom";
import Article from "../components/Article";
import { BounceLoader } from "react-spinners";
import { useFetchData } from "../Hooks/useFetchData";
import "../components/SectionPageStyle.css";

const SectionBottom = () => {

  const urlSection = `https://api.nytimes.com/svc/topstories/v2/food.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { load, articles } = useFetchData(urlSection);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4 mt-6">
        {articlesLoad.slice(6).map((article, index) => (
          <Article key={index} {...article} />
        ))}
      </div>
    );
  } else {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }
};

export default SectionBottom;
