import React from "react";
import { Link } from "react-router-dom";

const horizontalMenuItems = [
  "U.S.",
  "World",
  "Business",
  "Arts",
  "Lifestyle",
  "Opinion",
  "Audio",
  "Games",
  "Cooking",
  "Wirecutter",
  "The Athletic",
];

const HorizontalMenu = ({ setNav, nav }) => {
  return (
    <nav>
      <ul className="flex space-x-4">
        {horizontalMenuItems.map((item, index) => (
          <li key={index}>
            <Link
              to={`/feed/${item.toLowerCase()}`} // Aggiungi il parametro topic alla rotta
              onClick={() => setNav(!nav)} // Chiudi il menu al clic
              className="text-lg font-semibold"
            >
              {item}
            </Link>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default HorizontalMenu;
