import React from "react";
import "./pages/homepage.css";

const TwoColumnsText = ({ article }) => {
  return (
    <div className="two-columns-article w-1/2 px-2">
      <h3>{article.title}</h3>
      <p>{article.abstract}</p>
    </div>
  );
};

export default TwoColumnsText;
