import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { BounceLoader } from "react-spinners";
import axios from "axios";

const SearchResultsPage = () => {
  const { content } = useParams();
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  const searchURL = `https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${content}&api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  useEffect(() => {
    const fetchArticles = async () => {
      setLoading(true);
      setError(false);
      try {
        const response = await axios.get(searchURL);
        setArticles(response.data.response.docs || []);
      } catch (error) {
        console.error("Error fetching articles:", error);
        setError(true);
      } finally {
        setLoading(false);
      }
    };

    fetchArticles();
  }, [searchURL]);

  const formatDate = (date) => {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
    };

    return new Date(date).toLocaleDateString("en-EN", options);
  };

  if (loading) {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }

  if (error || articles.length === 0) {
    return (
      <div className="max-w-[1000px] mx-auto mt-2">
        <h2 className="ml-2">
          Showing results for:{" "}
          <span className="text-3xl font-bold capitalize">{content}</span>
        </h2>
        <p className="font-bold text-center mt-3 text-xl p-10">
          No search results found
        </p>
      </div>
    );
  }

  return (
    <div className="max-w-[840px] mx-auto mt-6"> {/* Aggiungi mx-auto per centrare */}
  <h2 className="ml-2 text-2xl font-bold text-gray-800">
    Showing results for:{" "}
    <span className="text-3xl text-blue-500 capitalize">{content}</span>
  </h2>

  {searchResults.docs.map((article, index) => {
    return (
      <article
        key={index}
        className="bg-white rounded-lg shadow-md overflow-hidden flex flex-col md:flex-row p-4 mb-6 hover:shadow-xl transition-shadow duration-300"
        role="article"
      >
        <img
          src={article.image}
          alt={article.title}
          className="w-full md:w-48 h-40 object-cover rounded-lg mb-4 md:mb-0"
        />
        <div className="flex-1 p-4">
          <h2 className="text-xl font-semibold text-gray-900 mb-2 hover:text-blue-500">
            {article.title}
          </h2>
          <div className="flex items-center text-sm text-gray-500 mb-3">
            <span>{article.byline.original}</span>
            <span className="mx-2">â€¢</span>
            <span>{formatDate(article.pub_date)}</span>
          </div>
          <p className="text-gray-600 mb-4">{article.abstract}</p>
          <div className="flex justify-between items-center">
            <button
              onClick={() => handleArticleClick(article)}
              className="inline-flex items-center text-indigo-600 hover:text-indigo-500 focus:outline-none focus:underline"
              aria-label={`Read more about ${article.title}`}
            >
              Read more
            </button>
            <a
              href={article.url}
              target="_blank"
              rel="noopener noreferrer"
              className="text-gray-500 hover:text-gray-700"
            >
              View on NY Times
            </a>
          </div>
        </div>
      </article>
    );
  })}
</div>

  );
};

export default SearchResultsPage;
