import React, { useState } from "react";
import { HiOutlineMenu, HiOutlineX, HiOutlineSearch } from "react-icons/hi";
import { useGlobalContext } from "../context";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const [nav, setNav] = useState(false);
  const [search, setSearch] = useState("");
  const [showSearch, setShowSearch] = useState(false);
  const [isFocused, setIsFocused] = useState(false);
  const [isSearchClicked, setIsSearchClicked] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleSearch = (e) => {
    e.preventDefault();
    e.target.reset();
    if (search) {
      setSearch("");
      navigate(`/search/${search}`);
    }
  };

  const handleClose = () => {
    setIsDropdownOpen(false);
  }; // chiusura menu account 

  // Get today date and format it
  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    weekday: "long",
  };
  const todayDate = new Date().toLocaleDateString("en-En", options);

  const { sections, formatSection } = useGlobalContext();

  if (nav === true) {
    document.body.style.overflowY = "hidden";
  } else {
    document.body.style.overflowY = "scroll";
  }

  return (
    <div className={nav === true ? "" : "top-0 max-w-[1200px] mx-auto border-b-2 border-double"}>
      <div className="flex flex-col">
        <div className=" h-10 md:h-16 lg:h-24 flex flex-row justify-start items-center mt-1">
          <div>
            <div className="flex items-center justify-start">
              <HiOutlineMenu
                className={
                  nav
                    ? "hidden"
                    : "text-2xl ml-3 cursor-pointer md:text-3xl lg:text-4xl lg:hidden"
                }
                onClick={() => setNav(true)}
              />
              <HiOutlineSearch
                style={{
                  width: "21px",
                  height: "21px",
                  transform: isSearchClicked ? "scale(1)" : "scale(1)"
                }}
                className={
                  nav
                    ? "hidden"
                    : "text-2xl cursor-pointer md:text-3xl lg:text-4xl"
                }
                onClick={() => {
                  setShowSearch(!showSearch);
                  setIsSearchClicked(!isSearchClicked);
                }}
              />
              {showSearch && (
                <div className={`w-full lg:ml-2 lg:flex items-center justify-start`}>
                  <form
                    className="w-full flex items-center justify-start"
                    onSubmit={handleSearch}
                    autoFocus
                    onChange={(e) => setSearch(e.target.value)}>
                    <input type="text" placeholder="SEARCH"style={{width: "182px", height: "31px", textAlign: "left", border: "3px solid" }}
                    className="w-50 py-1 px-2 rounded bg-transparent text-gray-700 mr-2 flex-grow text-center"onFocus={() => setIsFocused(true)}
                    onBlur={() => setIsFocused(false)}
                    autoFocus
                    />
                    <button
                      type="submit"
                      className="z-10 bg-slate-300 text-xs p-2 rounded text-white font-bold">
                      GO
                    </button>
                  </form>
                </div>
              )}
            </div>
            <p
              style={{ textAlign: "left", marginLeft: "0px" }}
              className={
                nav
                  ? "hidden"
                  : "hidden font-bold text-[4xl] lg:block lg:mt-2 "
              }>
              {todayDate}
            </p>
            <p
              style={{ textAlign: "left", marginLeft: "0px" }}
              className={
                nav
                  ? "hidden"
                  : "hidden font-bold text-[2xl] lg:block lg:mt-2 "
              }>
              Today's Paper
            </p>
          </div>
          <h1 className="z-0 absolute left-[25%] right-[25%] text-center font-ancient text-2xl md:text-4xl lg:text-6xl">
            <Link to={"/"}>
              <img
                src="/Title.png"  
                alt="The New York Times"
                className="w-[750px] h-[110px] object-contain"
              />
            </Link>
          </h1>
          <HiOutlineX
            className={
              nav
                ? "text-2xl md:text-3xl lg:text-4xl cursor-pointer absolute right-0 mr-2"
                : "hidden"
            }
            onClick={() => setNav(false)}
          />
        </div>
        <div className="h-[35px] bg-[#f7f7f7] border-[#e2e2e2] flex items-center lg:h-[60px] lg:bg-white">
          <p className="font-bold text-[0.7rem] ml-2 lg:hidden">{todayDate}</p>
          <ul className="hidden lg:flex flex-row mx-auto place-content-center justify-center  ">
            {sections.map((section, index) => {
              return (
                <li
                  key={index}
                  className="capitalize rounded-md px-2 py-1 text-sm text-center hover:bg-slate-100 active:bg-slate-200">
                  {section === "home" ? (
                    <Link to={"/"}>{formatSection(section)}</Link>
                  ) : (
                    <Link to={`section/${section}`}>
                      {formatSection(section)}
                    </Link>
                  )}
                </li>
              );
            })}

            <li className=" rounded-md px-2 py-1 text-sm hover:bg-slate-100 active:bg-gray-700">
              <Link to={"/feed"}>Feed</Link>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
