import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";

const ArticleSection = () => {
  const [articles, setArticles] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch(
          "https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS"
        );
        if (!response.ok) {
          throw new Error("Failed to fetch articles");
        }
        const data = await response.json();
        const formattedArticles = data.results.map((article, index) => ({
          id: index + 1,
          title: article.title,
          image: article.multimedia?.[0]?.url || "images.unsplash.com/photo-1453928582365-b6ad33cbcf64",
          category: article.section
        }));
        setArticles(formattedArticles);
        setIsLoading(false);
      } catch (err) {
        setError(err.message);
        setIsLoading(false);
      }
    };

    fetchArticles();
  }, []);

  if (isLoading) {
    return (
      <div className="max-w-3xl mx-auto p-6 text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-3xl mx-auto p-6 text-center text-red-600">
        <p>Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto p-6 space-y-6">
      {articles.map((article) => (
        <motion.div
          key={article.id}
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="flex items-center justify-between p-4 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300 cursor-pointer"
        >
          <div className="flex-1 pr-4">
            <span className="text-sm font-medium text-blue-600 mb-1 block capitalize">
              {article.category}
            </span>
            <h3 className="text-lg font-semibold text-gray-800 line-clamp-2 hover:text-blue-600 transition-colors duration-200">
              {article.title}
            </h3>
          </div>
          <div className="flex-shrink-0">
            <img
              src={`https://${article.image}`}
              alt={article.title}
              className="w-[90px] h-[90px] object-cover rounded-lg"
              onError={(e) => {
                e.target.src = "https://images.unsplash.com/photo-1453928582365-b6ad33cbcf64?ixlib=rb-4.0.3";
                e.target.alt = "Fallback image";
              }}
            />
          </div>
        </motion.div>
      ))}
    </div>
  );
};

export default ArticleSection;
