import React from "react";
import { Link } from "react-router-dom";
import "../article.css";

const Article = ({ title, multimedia, abstract, url }) => {
  return (
    <div className="container flex flex-col justify-center items-center w-[90%] mx-auto pb-2 border-b-2 border-gray-300">
      {/* Immagine */}
      {multimedia && multimedia[0] && (
        <div className="mt-4">
          <img
            className="w-full h-auto shadow-md"
            src={multimedia[0].url}
            alt={title}
          />
        </div>
      )}

      {/* Titolo */}
      <h2 className="font-extrabold uppercase text-lg w-full mx-auto mt-4 text-center">
        <Link to={url} target="_blank" rel="noreferrer">
          {title}
        </Link>
      </h2>

      {/* Abstract */}
      {abstract && (
        <div className="mt-4 text-justify w-full mx-auto px-4">
          <p className="text-sm">{abstract}</p>
        </div>
      )}
    </div>
  );
};

export default Article;
