import { useState, useEffect } from "react";
import { FaUser } from "react-icons/fa";
import { Link } from "react-router-dom";

const ArticlesProva = () => {
  const [isHovered, setIsHovered] = useState(false);
  const [articles, setArticles] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch("https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS");
        if (!response.ok) throw new Error("Failed to fetch articles");
        const data = await response.json();
        setArticles(data.results[0]);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchArticles();
  }, []);

  if (loading) return <div className="text-center p-6">Loading...</div>;
  if (error) return <div className="text-center p-6 text-red-500">{error}</div>;
  if (!articles) return <div className="text-center p-6">No article found</div>;

  return (
    <div
      className="max-w-[1000px] mx-auto p-6 bg-gradient-to-r from-gray-50 to-gray-100 rounded-xl shadow-lg transition-transform duration-300 hover:shadow-xl"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      tabIndex={0}
    >
      <div className="flex flex-col md:flex-row gap-8 items-center">
        <div className="flex-1 space-y-4">
        <Link to={articlesLoad[0].url}>
          <h2 
            className={`text-2xl font-bold text-gray-800 transition-colors duration-300 ${isHovered ? "text-blue-600" : ""}`}
            tabIndex={0}
          >
            {articles.title}
          </h2></Link>
          <p className="text-gray-600 leading-relaxed" tabIndex={0}>
            {articles.abstract}
          </p>
          <div 
            className="flex items-center space-x-3 text-gray-500"
            tabIndex={0}
          >
            <FaUser className="w-5 h-5" />
            <span className="font-medium">{articles.byline}</span>
          </div>
        </div>

        <div className="w-full md:w-[340px] relative group">
          <img
            src={articles.multimedia?.[0]?.url || "https://images.unsplash.com/photo-1633613286991-611fe299c4be"}
            alt={articles.multimedia?.[0]?.caption || "Article image"}
            className="w-full h-[240px] object-cover rounded-lg shadow-md transition-transform duration-300 group-hover:scale-[1.02]"
            onError={(e) => {
              e.target.src = "https://images.unsplash.com/photo-1633613286991-611fe299c4be";
              e.target.alt = "Fallback placeholder image";
            }}
            loading="lazy"
          />
        </div>
      </div>
    </div>
  );
};

export default ArticlesProva;
