import React from "react";
import { useFetchData } from "../Hooks/useFetchData";
import ArticleRightSlide from "../components/ArticleRightSlide";
import { BounceLoader } from "react-spinners";
import Article from "../components/Article";
import { Link } from "react-router-dom";
import "../pages/SectionPage";
import "../pages/homepage.css";
import GridFourTitle from "../components/GridFourTitle";

const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;
  const { load, articles } = useFetchData(urlHome);
  const articlesLoad = articles?.results || [];

  if (load) {
    return (
      <div className="homepage-container">
        {/* Colonna sinistra */}
        <div className="left-column">
          {/* Primo articolo con immagine a destra */}
          {articlesLoad.length > 0 && articlesLoad[0] && (
          <div className="article">
            <div className="text-content">
              <Link to={articlesLoad[0].url} className="article-title">
                <h2>{articlesLoad[0].title}</h2>
              </Link>
              <p>{articlesLoad[0].abstract}</p>
              <div className="divider"></div>

              {articlesLoad[1] && (
                <Link to={articlesLoad[1].url} className="article-title">
                <h2 className="article-title-live">{articlesLoad[1].title}</h2>
                </Link>
              )}
            </div>

            <img
              src={articlesLoad[1]?.multimedia?.[1]?.url || ""}
              alt={articlesLoad[0].title}
            />
          </div>
        )}
          <div className="divider"></div>

          <GridFourTitle articles={articlesLoad} />


          <div className="divider"></div>

          {/* Articolo con immagine a sinistra e metà testo */}
          {articlesLoad.length > 1 && (
            <div className="article half-width">
              <img
                src={articlesLoad[4].multimedia?.[0]?.url || ""}
                alt={articlesLoad[4].title}
              />
              <div className="text-content">
              <Link to={articlesLoad[0].url} className="article-title">
                <h2 className="article-title">{articlesLoad[4].title}</h2>
                </Link>
                <p>{articlesLoad[4].abstract}</p>
              </div>
            </div>
          )}

          <div className="divider"></div>

          {/* Articolo con immagine a sinistra e metà testo */}
          {articlesLoad.length > 6 && (
            <div className="article half-width">
              <img
                src={articlesLoad[6].multimedia?.[0]?.url || ""}
                alt={articlesLoad[6].title}
              />
              <div className="text-content">
              <Link to={articlesLoad[0].url} className="article-title">
                <h2 className="article-title">{articlesLoad[6].title}</h2>
                </Link>
                <p>{articlesLoad[6].abstract}</p>
              </div>
            </div>
          )}

          {/* Articolo solo titolo e testo */}
          {articlesLoad.length > 8 && (
            <div className="full-text">
              <Article
                section={articlesLoad[3].section}
                title={articlesLoad[3].title}
                abstract={articlesLoad[3].abstract}
                url={articlesLoad[3].url}
              />
              </div>)}


          {/* Altro articolo con immagine a destra */}
          {articlesLoad.length > 8 && (
            <div className="article half-width">
              <Article
                multimedia={articlesLoad[4].multimedia}
                title={articlesLoad[4].title}
                abstract={articlesLoad[4].abstract}
                url={articlesLoad[4].url}
                byline={articlesLoad[4].byline}
              />
              </div>
          )}

        </div>

        {/* Colonna destra */}
        <div className="right-column">
        {articlesLoad.length > 1 && (
            <div className="article half-width">
              <Article
                multimedia={articlesLoad[8].multimedia}
                title={articlesLoad[8].title}
                abstract={articlesLoad[8].abstract}
                url={articlesLoad[8].url}
                byline={articlesLoad[8].byline}
              />
            </div>
          )}
          <ArticleRightSlide articles={articlesLoad} />
        </div>
      </div>
    );
  } else {
    return <div>
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    </div>;
  }
};

export default Homepage;
