import React from "react";
import "./Homepage.css";

const Homepage = ({ news }) => {
  return (
    <div className="homepage-container">
      <div className="left-column">
        {/* Iteriamo sulle notizie principali */}
        {news.left.map((item, index) => (
          <div key={index} className="news-item">
            <h2>{item.title}</h2>
            <p>{item.description}</p>
          </div>
        ))}
      </div>
      <div className="right-column">
        {/* Iteriamo sulle notizie secondarie */}
        {news.right.map((item, index) => (
          <div key={index} className="news-item">
            <h3>{item.title}</h3>
            <p>{item.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Homepage;
