import React, { useState } from "react";
import { FaChevronDown, FaChevronUp } from "react-icons/fa";
import ErrorPage from "../pages/ErrorPage.js";

const Footer = () => {
  const [expandedSections, setExpandedSections] = useState({});
  const [showErrorPage, setShowErrorPage] = useState(false); // Stato per gestire la visualizzazione dell'ErrorPage

  const footerColumns = [
    {
      title: "News",
      links: ["Homepage", "U.S.", "World", "Politics", "NewYork", "Education"]
    },
    {
      title: "Arts",
      links: ["Book Reviews", "Best Seller Book List", "Dance", "Movies", "Music"]
    }
    // ... altre sezioni
  ];

  const toggleSection = (title) => {
    setExpandedSections((prev) => ({
      ...prev,
      [title]: !prev[title],
    }));
  };

  // Funzione per mostrare l'ErrorPage
  const handleLinkClick = () => {
    setShowErrorPage(true);
  };

  if (showErrorPage) {
    return <ErrorPage />; // Mostra l'ErrorPage se richiesto
  }

  return (
    <footer className="bg-white text-gray-900 py-12 px-4 sm:px-12 lg:px-[75px] shadow-lg">
      <div className="max-w-7xl mx-auto border-t-2 border-gray-300">
        <div className="grid grid-cols-1 lg:grid-cols-6 gap-8">
          {footerColumns.map((column) => (
            <div
              key={column.title}
              className="space-y-4"
              role="region"
              aria-label={`${column.title} section`}
            >
              <div
                className="flex justify-between items-center cursor-pointer lg:cursor-auto"
                onClick={() => toggleSection(column.title)}
              >
                <h2 className="text-[13px] md:text-xs font-bold tracking-wider uppercase border-b border-gray-100 pb-2 transition-colors duration-300 hover:text-blue-600 w-full md:w-auto">
                  {column.title}
                </h2>
                <span className="lg:hidden">
                  {expandedSections[column.title] ? <FaChevronUp /> : <FaChevronDown />}
                </span>
              </div>
              <ul className="hidden lg:block space-y-2">
                {column.links.map((link, linkIndex) => (
                  <li key={linkIndex}>
                    <button
                      onClick={handleLinkClick} // Gestisci il click
                      className="text-[12px] text-black hover:text-gray-800 transition-colors duration-300 block py-1 focus:outline-none focus:ring-2 focus:ring-blue-300 focus:ring-offset-2 focus:ring-offset-white rounded"
                    >
                      {link}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </footer>
  );
};

export default Footer;
