import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import Article from "../components/articles/OneArticle";
import { BounceLoader } from "react-spinners";
import axios from "axios";
import "../pages/homepage.css";

const SectionPage = () => {
  const { nameSection } = useParams();
  const [articles, setArticles] = useState([]);
  const [load, setLoad] = useState(true);

  const urlSection = `https://api.nytimes.com/svc/topstories/v2/news.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get(urlSection);
        setArticles(response.data.results);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoad(false);
      }
    };

    fetchArticles();
  }, [urlSection]);

  if (load) {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }

  if (!articles || articles.length === 0) {
    return <div>No articles available</div>;
  }

  return (
    <div className="max-w-screen-xl mx-auto px-4 py-4 myfont">
      <h2 className="font-bold ml-2 mt-2 text-xl md:text-2xl lg:text-3xl capitalize">
        {nameSection.charAt(0).toUpperCase() + nameSection.slice(1)} News
      </h2>

      {/* Section grid */}
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4 mt-6">
        {articles.slice(6).map((article, index) => (
          <Article key={index} {...article} />
        ))}
      </div>
    </div>
  );
};

export default SectionPage;
