import React from "react";
import { BounceLoader } from "react-spinners";
import Article from "../components/Article";
import { useFetchData } from "../Hooks/useFetchData";
import "../homepage.css";

const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { load, articles } = useFetchData(urlHome);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="homepage-container">
        {/* Colonna sinistra */}
        <div className="left-column">
          {/* Primo articolo con immagine a destra */}
          {articlesLoad.length > 0 && (
            <div className="first-article">
              <img
                src={articlesLoad[0].multimedia[0]?.url || ""}
                alt={articlesLoad[0].title}
                className="article-image"
              />
              <div className="article-text">
                <h2>{articlesLoad[0].title}</h2>
                <p>{articlesLoad[0].abstract}</p>
              </div>
            </div>
          )}

          {/* Successivi due articoli */}
          <div className="next-articles">
            {articlesLoad.slice(1, 3).map((article, index) => (
              <div key={index} className="small-article">
                <h3>{article.title}</h3>
                <p>{article.abstract}</p>
              </div>
            ))}
          </div>
          {index < 2 - 1 && <div className="article-divider" />}
        </div>

        {/* Colonna destra */}
        <div className="right-column">
          {/* Sidebar o contenuti aggiuntivi */}
          <div className="sidebar">
            {articlesLoad.slice(3, 8).map((article, index) => (
              <Article key={index} {...article} />
            ))}
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <div className="flex justify-center items-center h-screen">
        <BounceLoader color="gray" size={180} aria-label="Loading Spinner" />
      </div>
    );
  }
};

export default Homepage;
