import React from "react";
import "../pages/homepage.css"; // Aggiungi qui il file CSS specifico per il componente

const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;
  const { load, articles } = useFetchData(urlHome);
  const articlesLoad = articles?.results || [];

const GridFourTitles = ({ articles }) => {
  // Controlla che ci siano almeno 4 articoli da mostrare
  const displayedArticles = articles.slice(1, 5);

  if (displayedArticles.length === 0) {
    return <div>No articles available</div>;
  }

  return (
    <div className="grid-four-titles">
      {displayedArticles.map((article, index) => (
        <div className="article-title-only" key={index}>
          <div className="full-text">
              <Article
                title={articlesLoad[3].title}
                abstract={articlesLoad[3].abstract}
                url={articlesLoad[3].url}
              />
              </div>
        </div>
      ))}
    </div>
  );
};
}

export default GridFourTitles;
