import React from "react";
import { Link } from "react-router-dom";
import "../components/RightSideArticle.css";

const RightSideArticle = ({
  title,
  byline,
  multimedia,
  abstract,
  url,
}) => {

  return (
    <div className="article-container">
      {/* Sezione e Titolo */}

      {/* Immagine */}
      {multimedia && multimedia[0] && (
        <div className="article-header">
          <img
            className="w-full h-auto max-h-60 object-cover shadow-md"
            src={multimedia[0].url}
            alt={title}
          />
          {byline && (
            <p className="mt-2 self-end text-xs font-bold">{byline}</p>
          )}
        </div>
      )}

      {/* Titolo */}
      <h2 className="article-header h2">
        <Link to={url} target="_blank" rel="noreferrer">
          {title}
        </Link>
      </h2>

      {/* Abstract */}
      {abstract && (
        <div className="mt-4 text-justify w-full mx-auto pb-[10px]">
          <p className="text-sm">{abstract}</p>
        </div>
      )}

      {/* Data */}
    </div>
  );
};

export default RightSideArticle;
