import { useState, useEffect } from "react";
import { FaUser, FaCalendar, FaExternalLinkAlt } from "react-icons/fa";

const SearchResults = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const [searchQuery, setSearchQuery] = useState("election");

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch(
          `https://api.nytimes.com/svc/search/v2/articlesearch.json?q=${searchQuery}&api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`
        );
        const data = await response.json();
        const formattedArticles = data.response.docs.map(article => ({
          id: article._id,
          title: article.headline.main,
          author: article.byline?.original || "Unknown Author",
          date: article.pub_date,
          snippet: article.snippet,
          content: article.lead_paragraph,
          url: article.web_url
        }));
        setArticles(formattedArticles);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch articles. Please try again later.");
        setLoading(false);
      }
    };

    fetchArticles();
  }, [searchQuery]);

  const handleArticleClick = (article) => {
    setSelectedArticle(article);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-bounce w-16 h-16 border-4 border-indigo-600 border-t-transparent rounded-full"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="bg-red-50 p-6 rounded-lg shadow-md">
          <h2 className="text-red-600 text-xl font-semibold mb-2">Error</h2>
          <p className="text-red-500">{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">NY Times Article Search</h1>
          <form onSubmit={handleSearch} className="flex gap-4">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="flex-1 p-2 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
              placeholder="Search articles..."
            />
            <button
              type="submit"
              className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
            >
              Search
            </button>
          </form>
        </div>

        {articles.length === 0 ? (
          <div className="bg-white p-6 rounded-lg shadow-md">
            <p className="text-gray-500 text-center">No articles found.</p>
          </div>
        ) : (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {articles.map((article) => (
              <article
                key={article.id}
                className="bg-white rounded-lg shadow-md overflow-hidden transition-transform duration-300 hover:transform hover:scale-102 hover:shadow-lg focus-within:ring-2 focus-within:ring-indigo-500"
                tabIndex="0"
                role="article"
              >
                <div className="p-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-2">
                    {article.title}
                  </h2>
                  <div className="flex items-center text-sm text-gray-500 mb-3">
                    <div className="flex items-center mr-4">
                      <FaUser className="mr-1" />
                      <span>{article.author}</span>
                    </div>
                    <div className="flex items-center">
                      <FaCalendar className="mr-1" />
                      <span>{new Date(article.date).toLocaleDateString()}</span>
                    </div>
                  </div>
                  <p className="text-gray-600 mb-4">{article.snippet}</p>
                  <div className="flex justify-between items-center">
                    <button
                      onClick={() => handleArticleClick(article)}
                      className="inline-flex items-center text-indigo-600 hover:text-indigo-500 focus:outline-none focus:underline"
                      aria-label={`Read more about ${article.title}`}
                    >
                      Read more
                      <FaExternalLinkAlt className="ml-1 w-4 h-4" />
                    </button>
                    <a
                      href={article.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-gray-500 hover:text-gray-700"
                    >
                      View on NY Times
                    </a>
                  </div>
                </div>
              </article>
            ))}
          </div>
        )}

        {selectedArticle && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
              <div className="p-6">
                <h2 className="text-2xl font-bold mb-4">{selectedArticle.title}</h2>
                <div className="flex items-center text-sm text-gray-500 mb-4">
                  <div className="flex items-center mr-4">
                    <FaUser className="mr-1" />
                    <span>{selectedArticle.author}</span>
                  </div>
                  <div className="flex items-center">
                    <FaCalendar className="mr-1" />
                    <span>
                      {new Date(selectedArticle.date).toLocaleDateString()}
                    </span>
                  </div>
                </div>
                <p className="text-gray-600 mb-6">{selectedArticle.content}</p>
                <div className="flex justify-between">
                  <button
                    onClick={() => setSelectedArticle(null)}
                    className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                    aria-label="Close article"
                  >
                    Close
                  </button>
                  <a
                    href={selectedArticle.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-500 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
                  >
                    Read on NY Times
                  </a>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchResults;
