import React from "react";
import { useGlobalContext } from "../context";

const ArticleSearch = ({ articles, handleArticleClick }) => {
  const { formatSection } = useGlobalContext();

  const formatDate = (date) => {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
    };

    return new Date(date).toLocaleDateString("en-En", options);
  };

  return (
    <div className="max-w-[840px] mx-auto space-y-6">
      {articles?.map((article) => (
        <article
          key={article.title}
          className="bg-white rounded-lg shadow-md overflow-hidden flex"
          role="article"
        >
          <img
            src={article.image || "https://via.placeholder.com/205x195"}
            alt={article.title}
            className="w-[205px] h-[195px] object-cover"
          />
          <div className="flex-1 p-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-2">
              {article.title}
            </h2>
            <div className="flex items-center text-sm text-gray-500 mb-3">
              <div className="flex items-center mr-4">
                <span>{article.byline?.original || "Author Unknown"}</span>
              </div>
              <div className="flex items-center">
                <span>{formatDate(article.pub_date)}</span>
              </div>
            </div>
            <p className="text-gray-600 mb-4">{article.abstract}</p>
            <div className="flex justify-between items-center">
              <button
                onClick={() => handleArticleClick?.(article)}
                className="inline-flex items-center text-indigo-600 hover:text-indigo-500 focus:outline-none focus:underline"
                aria-label={`Read more about ${article.title}`}
              >
                Read more
              </button>
              <a
                href={article.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-gray-500 hover:text-gray-700"
              >
                View on NY Times
              </a>
            </div>
          </div>
        </article>
      ))}
    </div>
  );
};

export default ArticleSearch;
