import React from "react";
import "../pages/homepage.css";

const ImageRight = ({ 
  section,
  published_date,
  title,
  byline,
  multimedia,
  abstract,
  url,
 }) => {
  return (
    <div className="image-right-container flex mb-8">
      <div className="text-content w-1/2 pr-4">
        <h2>{article.title}</h2>
        <p>{article.abstract}</p>
      </div>
      <img
        src={article.multimedia[0]?.url || ""}
        alt={article.title}
        className="article-image w-1/2"
      />
    </div>
  );
};

export default ImageRight;
