import React from "react";
import { BounceLoader } from "react-spinners";
import { useFetchData } from "../Hooks/useFetchData";
import "../components/SectionPageStyle.css";

const SectionBottom = () => {
  const urlSection = `https://api.nytimes.com/svc/topstories/v2/well.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;

  const { load, articles } = useFetchData(urlSection);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="section-container mt-6 p-4">
        {/* Titolo della sezione */}
        <h2 className="text-lg font-bold text-gray-800 mb-6">WELL</h2>
        <div className="grid grid-cols-1 md:grid-cols-[2fr_1fr] gap-6">
          {/* Colonna sinistra */}
          <div className="left-column space-y-6">
            {articlesLoad.slice(0, 3).map((article, index) => (
              <div
                key={index}
                className="flex flex-col md:flex-row items-start md:items-center gap-4 bg-white p-4 rounded-lg shadow"
              >
                {/* Testo articolo */}
                <div className="flex-1">
                  <h3 className="text-lg font-bold">
                    <a
                      href={article.url}
                      target="_blank"
                      rel="noreferrer"
                      className="hover:text-blue-600"
                    >
                      {article.title}
                    </a>
                  </h3>
                  <p className="text-sm text-gray-600 mt-2">
                    {article.abstract}
                  </p>
                </div>
                {/* Immagine allineata a destra */}
                <div className="flex-shrink-0 md:w-1/3">
                  <img
                    src={article.multimedia?.[0]?.url || "/placeholder.jpg"}
                    alt={article.title}
                    className="w-full h-auto rounded"
                  />
                </div>
              </div>
            ))}
          </div>

          {/* Colonna destra */}
          <div className="right-column space-y-4">
            {articlesLoad.slice(3, 9).map((article, index) => (
              <div
                key={index}
                className="bg-white p-3 rounded-lg shadow hover:shadow-md transition-shadow duration-200"
              >
                <h4 className="text-sm font-bold">
                  <a
                    href={article.url}
                    target="_blank"
                    rel="noreferrer"
                    className="hover:text-blue-600"
                  >
                    {article.title}
                  </a>
                </h4>
                <p className="text-xs text-gray-500 mt-1">
                  {article.byline || "Author Unknown"}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <BounceLoader
        className="mx-auto my-36"
        color="gray"
        size={180}
        aria-label="Loading Spinner"
      />
    );
  }
};

export default SectionBottom;
