import React from "react";
import { motion } from "framer-motion";

const ArticleGrid = () => {
  const articles = [
    {
      id: 1,
      title: "Exploring Nature's Wonders",
      image: "images.unsplash.com/photo-1682686580391-615b1e32546e",
      alt: "Beautiful mountain landscape with sunset"
    },
    {
      id: 2,
      title: "Urban Architecture Today",
      image: "images.unsplash.com/photo-1682686580186-b55d2a91053c",
      alt: "Modern city buildings with glass facades"
    },
    {
      id: 3,
      title: "Culinary Adventures",
      image: "images.unsplash.com/photo-1682686580950-960d1d513532",
      alt: "Delicious gourmet dish presentation"
    },
    {
      id: 4,
      title: "Technology Innovation",
      image: "images.unsplash.com/photo-1682687220742-aba13b6e50ba",
      alt: "Futuristic technology concept"
    },
    {
      id: 5,
      title: "Sustainable Living",
      image: "images.unsplash.com/photo-1682687220063-3cdb7eea0a12",
      alt: "Eco-friendly lifestyle choices"
    },
    {
      id: 6,
      title: "Art & Culture",
      image: "images.unsplash.com/photo-1682687220923-c58b9a4592ae",
      alt: "Contemporary art exhibition"
    },
    {
      id: 7,
      title: "Travel Destinations",
      image: "images.unsplash.com/photo-1682687220199-d0124f48f95b",
      alt: "Scenic travel destination"
    },
    {
      id: 8,
      title: "Fitness & Wellness",
      image: "images.unsplash.com/photo-1682687220509-61b8a906ca19",
      alt: "Modern fitness equipment"
    },
    {
      id: 9,
      title: "Digital Trends",
      image: "images.unsplash.com/photo-1682687220566-c9dbed4bce02",
      alt: "Digital technology trends"
    },
    {
      id: 10,
      title: "Fashion Forward",
      image: "images.unsplash.com/photo-1682687220801-eeba6f49b134",
      alt: "Contemporary fashion styles"
    }
  ];

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-blue-50 to-indigo-50 p-8">
      <section className="max-w-[840px] mx-auto">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
          {articles.map((article) => (
            <motion.article
              key={article.id}
              className="bg-white rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-shadow duration-300 w-full"
              whileHover={{ scale: 1.03 }}
              transition={{ duration: 0.2 }}
            >
              <div className="relative overflow-hidden aspect-[4/3]">
                <motion.img
                  src={`https://${article.image}`}
                  alt={article.alt}
                  className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
                  loading="lazy"
                  whileHover={{ scale: 1.1 }}
                  onError={(e) => {
                    e.target.src = "https://images.unsplash.com/photo-1682686581660-3693f0c588d2";
                    e.target.alt = "Fallback image";
                  }}
                />
              </div>
              <div className="p-4">
                <h2 className="text-lg font-semibold text-gray-800 hover:text-indigo-600 transition-colors duration-300">
                  {article.title}
                </h2>
              </div>
            </motion.article>
          ))}
        </div>
      </section>
    </div>
  );
};

export default ArticleGrid;
