import React from "react";
import { useFetchData } from "../Hooks/useFetchData";
import "../pages/homepage.css";

const truncateText = (text, maxLength) => {
  if (!text) return "";
  return text.length > maxLength ? text.slice(0, maxLength) + "..." : text;
};

const Homepage = () => {
  const urlHome = `https://api.nytimes.com/svc/topstories/v2/home.json?api-key=30mS33uUBWOVAYffuOfb9rpPZhDzpDjS`;
  const { load, articles } = useFetchData(urlHome);

  const articlesLoad = articles.results;

  if (load) {
    return (
      <div className="homepage-container max-w-[1000px] mx-auto flex justify-between mt-4">
        {/* Colonna sinistra */}
        <div className="left-column">
          {/* Primo articolo */}
          {articlesLoad.length > 0 && (
            <div className="article">
              <h2 className="article-title">{truncateText(articlesLoad[0].title, 60)}</h2>
              <p className="article-text">{truncateText(articlesLoad[0].abstract, 150)}</p>
            </div>
          )}

          {/* Secondo e terzo articolo uno accanto all'altro */}
          <div className="article-row">
            {articlesLoad.slice(1, 3).map((article, index) => (
              <div key={index} className="article">
                <h2 className="article-title">{truncateText(article.title, 50)}</h2>
                <p className="article-text">{truncateText(article.abstract, 100)}</p>
              </div>
            ))}
          </div>

          {/* Articoli successivi */}
          {articlesLoad.slice(3).map((article, index) => (
            <React.Fragment key={index}>
              <div className="article">
                <h2 className="article-title">{truncateText(article.title, 60)}</h2>
                <p className="article-text">{truncateText(article.abstract, 150)}</p>
              </div>
              <div className="article-divider"></div>
            </React.Fragment>
          ))}
        </div>
      </div>
    );
  } else {
    return <div>Loading...</div>;
  }
};

export default Homepage;
